  public message = '';
  public messages: Message[] = [];
  private interval: any;


  constructor(){ }

  ngOnInit(): void {
    this.messageReceived.subscribe(
      data => this.messages = [ { message: data, timeEntered: Date.now()}, ...this.messages ]
