    this.interval = setInterval(() => this.messages = this.messages.filter(m => (Date.now() - m.timeEntered) < 90000 ), 60000);
  }

  public addMessage(): void {
    this.messageEntered.emit(this.message);
  }

}
interface Message {
  message: string;
  timeEntered: number;
}

